/*global describe, it, expect*/
const server = require("./server.js");

describe("Server", function() {
  it("should format object to JSON string", () => {
    expect(server.prepareJson({ key1: "val1", key2: "val2" })).toBe(
      '{"key1":"val1","key2":"val2"}'
    );
  });

  it("should not be longer than 500 chars", () => {
    const longStr =
      "";
    expect(server.prepareJson({ key1: "val1", key2: longStr })).toBe(
      '{"key1":"val1","key2":"'
    );
  });
});
